/** Specifies the settings for capitalization. */
export enum CapitalizationSettings {
    /** All letters are changed to uppercase. */
    ToUpper,
    /** All letters are changed to lowercase. */
    ToLower,
    /** The first letter of each word is capitalized. */
    CapitalizeEachWord,
    /** The first letter of each sentence is capitalized. */
    CapitalizeEachSentence,
    /** Lowercase letters are changed to uppercase and vice versa. */
    ToggleCase,
}
