declare namespace TXTextControl {
    /** Specifies whether the lowercase letters of text are displayed with capital letters. */
    enum Capitals {
        /** All lowercase letters are displayed with capital letters. */
        Capitals,
        /** All lowercase letters are displayed with capital letters which have the size of the lowercase 'x' + 10%. */
        SmallCapitals,
        /** All lowercase letters are displayed with capital letters which have the size of the lowercase 'x'. */
        PetiteCapitals,
        /** There are no text effects with capital letters. */
        None
    }
}	