declare namespace TXTextControl {
    /** Determines the writing direction of a paragraph. */
    enum Direction {
        /** 
         * The writing direction is left to right, 
         * which means that blocks of text with left to right and right to left characters are ordered from left to right.
         */
        LeftToRight,
        /** 
         * The writing direction is right to left, 
         * which means that blocks of text with left to right and right to left characters are ordered from right to left.
         */
        RightToLeft
    }
}