declare namespace TXTextControl {
    /** Specifies the kind of justification. */
    enum Justification {
        /** To justify a line additional space is added to the spaces between the words. */
        Spaces,
        /** To justify a line Kashida lines are inserted. */
        Kashida,
        /** To justify a line both kinds of justification are used. */
        SpacesAndKashida
    }
}