declare namespace TXTextControl {
    /** Specifies the list format character. */
    enum ListFormatCharacter {
        /** The text of the list immediately follows the bullet or number character. */
        None,
        /** The text of the list follows the bullet or number character at the next tab position. */
        Tab,
        /** The text of the list follows the bullet or number character separated through a space character. */
        Space
    }
}